    private void lihatButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_lihatButtonActionPerformed
        // TODO add your handling code here:
        mataKuliahController.tampilkanDaftar();
    }//GEN-LAST:event_lihatButtonActionPerformed

    private void kodematakuliahTextFieldKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_kodematakuliahTextFieldKeyPressed
        // TODO add your handling code here:
        if (evt.getKeyCode() == KeyEvent.VK_ENTER){
            mataKuliahController.cari(kodematakuliahTextField);
        }
    }//GEN-LAST:event_kodematakuliahTextFieldKeyPressed

    private void simpanButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_simpanButtonActionPerformed
        // TODO add your handling code here:
        mataKuliahController.simpan(kodematakuliahTextField, namaMataKuliahTextField, jumlahsksComboBox);
    }//GEN-LAST:event_simpanButtonActionPerformed

    private void hapusButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_hapusButtonActionPerformed
        // TODO add your handling code here:
        mataKuliahController.hapus(kodematakuliahTextField);
    }//GEN-LAST:event_hapusButtonActionPerformed


